import { TestBed } from '@angular/core/testing';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';

import { ImageService } from './image.service';
import { Image } from '../models/image';

const BASE64_IMAGE = '';
const DATA_URL = `data:image/jpeg;base64,${BASE64_IMAGE}`;

describe('ImageService', () => {
  let domSanitizerSpy: any;
  let imageService: ImageService;

  beforeEach(() => {
    domSanitizerSpy = jasmine.createSpyObj('DomSanitizer', ['bypassSecurityTrustUrl']);

    TestBed.configureTestingModule({
      providers: [{ provide: DomSanitizer, useValue: domSanitizerSpy }]
    });

    imageService = TestBed.get(ImageService);
  });

  it('should be created', () => {
    expect(imageService).toBeTruthy();
  });

  describe('processImage',
    () => {
      let fileReaderSpy: FileReader;
      let onloadSpy: Function;
      let expectedFile: File;

      beforeEach(() => {
        fileReaderSpy = jasmine.createSpyObj('FileReader', ['readAsDataURL', 'onload', 'result']);
        Object.defineProperty(fileReaderSpy, 'result', { value: DATA_URL });
        onloadSpy = fileReaderSpy.onload;

        spyOn(window as any, 'FileReader').and.returnValue(fileReaderSpy);

        expectedFile = { name: 'black hole.jpg', type: 'image/jpeg' } as File;
      });

      it('should call readAsDataUrl correctly', () => {
        imageService.processImage(expectedFile).subscribe();

        expect(fileReaderSpy.readAsDataURL).toHaveBeenCalledWith(expectedFile);
      });

      it('should subscribe to onload', () => {
        imageService.processImage(expectedFile).subscribe();

        expect(fileReaderSpy.onload).not.toEqual(onloadSpy);
      });

      it('should should return expected image', () => {
        const expectedImage = new Image();
        expectedImage.name = 'black hole.jpg';
        expectedImage.contentType = 'image/jpeg';
        expectedImage.file = BASE64_IMAGE;

        imageService.processImage(expectedFile).subscribe(result => {
          expect(result).toEqual(expectedImage);
        });

        fileReaderSpy.onload(null);
      });
    });

  describe('sanitize',
    () => {
      let image: Image;
      let expectedUnsafeUrl: string;
      let expectedSafeUrl: SafeUrl;

      beforeEach(() => {
        image = new Image();
        image.name = 'black hole.jpg';
        image.contentType = 'image/jpeg';
        image.file = BASE64_IMAGE;

        expectedUnsafeUrl = DATA_URL;

        expectedSafeUrl = { value: 'test' } as SafeUrl;
      });

      it('should call bypassSecurityTrustUrl correctly', () => {
        imageService.sanitize(image);

        expect(domSanitizerSpy.bypassSecurityTrustUrl).toHaveBeenCalledTimes(1);
        expect(domSanitizerSpy.bypassSecurityTrustUrl).toHaveBeenCalledWith(expectedUnsafeUrl);
      })

      it('should return expected SafeUrl', () => {
        domSanitizerSpy.bypassSecurityTrustUrl.and.returnValue(expectedSafeUrl);

        const result = imageService.sanitize(image);

        expect(result).toEqual(expectedSafeUrl);
      })
    });
});
